@RequestMapping("/api/aop")
public class AopController {

    private final AspectLifecycleManager aspectLifecycleManager;

    @Autowired
    public AopController(AspectLifecycleManager aspectLifecycleManager) {
        this.aspectLifecycleManager = aspectLifecycleManager;
    }

    @PostMapping("/add")
