        private boolean isLoadedAspect(String className) {
            return loadedAspects.stream()
                .anyMatch(aspect -> aspect.getClass().getName().equals(className));
        }

        private void unloadAspect(String className) {
            loadedAspects.removeIf(aspect -> aspect.getClass().getName().equals(className));
        }

        private boolean isAspectClass(String className) {
            return className.endsWith("Aspect");
