        private boolean isLoadedAspect(String className) {
            return loadedAspects.stream()
                .anyMatch(aspect -> aspect.getClass().getName().equals(className));
        }

        private void loadAspect(String className) {
            if (!isAspectClass(className)) {
                return;
            }

            if (isLoadedAspect(className)) {
                return;
            }

            try {
                Class<?> aspectClass = Class.forName(className);
                Object aspectInstance = applicationContext.getBean(aspectClass);
                loadedAspects.add(aspectInstance);
            } catch (ClassNotFoundException e) {
                throw new RuntimeException("Unable to load aspect: " + className, e);
            }
        }

        private void unloadAspect(String className) {
            if (isLoadedAspect(className)) {
                loadedAspects.removeIf(aspect -> aspect.getClass().getName().equals(className));
            }
        }

        private boolean isLoadedAspect(String className) {
            return loadedAspects.stream()
                .anyMatch(aspect -> aspect.getClass().getName().equals(className));
        }

        private void unloadAspect(String className) {
            if (!isLoadedAspect(className)) {
                return;
            }

            // Remove the aspect from the loaded aspects list
            loadedAspects.removeIf(aspect -> aspect.getClass().getName().equals(className));

            // Remove the aspect bean from the application context
            applicationContext.getAutowireCapableBeanFactory().removeBeanDefinition(className);
        }

        private void loadAspect(String className) {
            if (isAspectClass(className)) {
                if (!isLoadedAspect(className)) {
                    try {
                        Class<?> aspectClass = Class.forName(className);
                        Object aspectInstance = applicationContext.getBean(aspectClass);
                        loadedAspects.add(aspectInstance);
                    } catch (ClassNotFoundException e) {
                        throw new RuntimeException("Could not load aspect: " + className, e);
                    }
                }
            }
        }

        private boolean isLoadedAspect(String className) {
            return loadedAspects.stream()
                .anyMatch(aspect -> aspect.getClass().getName().equals(className));
        }

        private void loadAspect(String className) {
            if (isLoadedAspect(className)) {
                return;
            }

            try {
                Class<?> aspectClass = Class.forName(className);
                if (isAspectClass(aspectClass)) {
                    loadedAspects.add(aspectClass.newInstance())
                }
            }
        }

        private boolean isAspectClass(Class<?> aspectClass) {
            return aspectClass.isAnnotationPresent(Aspect.class);
        }

        private boolean isLoadedAspect(String className) {
            return loadedAspects.stream()
                .anyMatch(aspect -> aspect.getClass().getName().equals(className));
        }

        private void loadAspect(String className) {
            if (isAspectClass(className)) {
                try {
                    Class<?> aspectClass = Class.forName(className);
                    AspectJMethodMatcher methodMatcher = new AspectJMethodMatcher(aspectClass);
                    List<AspectJAdvice> advices = new ArrayList<>();
                    for (Method methodSignature signature : methodSignatures) {
                        if (methodMatcher.matches(signature)) {
                            AspectJAdvice advice = new AspectJAdvice(aspectClass, signature, methodSignatures.get(signature));
                            advices.add(advice);
                        }
                    }
                } catch (Exception e) {
                    throw new RuntimeException("Error loading aspect: " + className, e);
                }
            }
        }

        private boolean isAspectClass(Class<?> aspectClass) {
            return aspectClass.isAnnotationPresent(Aspect.class);
        }

        private void loadAspect(String className) {
            try {
                Class<?> aspectClass = Class.forName(className);
                if (isAspectClass(aspectClass)) {
                    Object aspectInstance = applicationContext.getBean(aspectClass);
                    loadedAspects.add(aspectInstance);
                }
            }
        }

        private boolean isLoadedAspect(Object aspectInstance) {
            return loadedAspects.contains(aspectInstance);
        }

        private boolean isAspectClass(Class<?> aspectClass) {
            return aspectClass.isAnnotationPresent(Aspect.class);
        }

        private void loadAspect(String className) {
            if (!isLoadedAspect(className)) {
                Class<?> aspectClass = Class.forName(className);
                if (isAspectClass(aspectClass)) {
                    loadedAspects.add(aspectClass.newInstance());
                }
            }
        }

        private boolean isAspectClass(Class<?> aspectClass) {
            return isAspectClass(aspectClass, applicationContext);
        }

        private boolean isAspectClass(Class<?> aspectClass, ApplicationContext applicationContext) {
            if (aspectClass.isAnnotationPresent(Aspect.class)) {
                return true;
            }

            // Check if the aspect is already loaded
            if (applicationContext.containsBean(aspectClass.getName())) {
                return true;
            }

            return false;
