        if (env.getProperty("enable.myAspect").equals("true")) {
            return new MyAspect();
        } else {
            return null; // or a no-op aspect
        }
    }

    @Bean
    public AnotherAspect anotherAspect() {
        if (env.getProperty("enable.anotherAspect").equals("true")) {
            return new AnotherAspect();
        } else {
            return null; // or a no-op aspect
        }
    }
}

